<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE html><html><head><title></title><meta charset="utf-8"><link rel="stylesheet" type="text/css" href="__PUBLIC__/css/common.css"></head><body><table align="center"><tr><th colspan="8"><?php if(ACTION_NAME == "articalList"): ?><h2>全部文章列表</h2><?php else: ?><h2>回收站文章列表</h2><?php endif; ?></th></tr><tr><th>ID</th><th>标题</th><th>作者</th><th>时间</th><th>点击次数</th><th>所属分类</th><th>所属标签</th><th>操作</th></tr><?php if(is_array($data)): foreach($data as $key=>$v): ?><tr><td><?php echo ($v["id"]); ?></td><td><?php if(is_array($v["attr"])): foreach($v["attr"] as $key=>$a): ?><span style="color:<?php echo ($a["color"]); ?>;">[<?php echo ($a["name"]); ?>]</span>&nbsp;<?php endforeach; endif; echo ($v["title"]); ?></td><td style="color:green;"><?php echo ($v["name"]); ?></td><td><?php echo (date('Y-m-d H:i', $v["time"])); ?></td><td><?php echo ($v["clicknum"]); ?></td><td style="color:blue;"><?php echo ($v["sort"]); ?></td><td><?php if(is_array($v["tags"])): foreach($v["tags"] as $key=>$t): ?><span style="background:gray;color:white;">[<?php echo ($t["name"]); ?>]</span>&nbsp;<?php endforeach; endif; ?></td><td><?php if(ACTION_NAME == "articalList"): ?><a href="">[编辑]</a><a href="<?php echo U( GROUP_NAME. '/Artical/toDel', array('id' => $v['id'], 'del' => 1) );?>" style="color:red;">[删除]</a><?php else: ?><a href="<?php echo U(GROUP_NAME.'/Artical/toDel', array('id' => $v['id'], 'del' => 0));?>">[还原]</a><a href="<?php echo U(GROUP_NAME.'/Artical/deleteArtical', array('id' => $v['id']));?>">[彻底删除]</a><?php endif; ?></td></tr><?php endforeach; endif; ?><tr><td colspan="8"><?php echo ($page); ?></td></tr></table></form></body></html>