<?php if (!defined('THINK_PATH')) exit(); if(is_array($tags)): foreach($tags as $key=>$tag): echo ($id=''); $num = $tag['num']; if(is_array($tag["bid"])): foreach($tag["bid"] as $key=>$b): $id.=$b['bid'].'_'; endforeach; endif; $id.=$tag['id']; ?><a href="<?php echo U('/list_'.$id);?>"><span><?php echo (tagfont($num,$tag["name"])); ?>(<?php echo ($num); ?>)</span></a><?php endforeach; endif; ?>